// Functions Functions Functions

function randomNumber() {

    var ranNum1 = 0;
    ranNum1 = Math.floor((Math.random() * 102) + 19);
    
    return ranNum1;
    
    }


function randomCryNumber() {

    var ranCrynum = 0;
    ranCrynum = Math.floor((Math.random() * 12) + 1);
    
    return ranCrynum;
    
    }
